   var dragChat = $('.chat-btn-wrap');
        dragChat.draggable({
          containment: "body",
          stop: function() {
             var docWidth = $(document).width();
             var docHeight = $(window).height();
             var dragchatX = dragChat.offset().left;
             var dragchatY = dragChat.position().top;
             var res_x= dragchatX/docWidth *100;
             var res_y= dragchatY/docHeight *100;
             $(this).removeAttr('style');
             $(this).css('left', res_x + '%');
             if( String(res_y).indexOf('-') == 0){
               $(this).css('top', '0%');
             }else if(parseInt(res_y) > 100){
               $(this).css('top', '90%');
             }else{
                $(this).css('top', res_y + '%');
             }
            $(this).css({'right' : 'auto', 'bottom' : 'auto'}); 
          }
        });
